package com.simplilearn.typecasting;

public class TypeCastDemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int amount=1000;
		float floamount=amount;
		double doubleamount=amount;
		long longamount=amount;
		System.out.println("int amount"+amount);
		System.out.println("float amount"+floamount);
		System.out.println("double amount"+doubleamount);
		System.out.println("long amount"+longamount);
		System.out.println("");
		
		byte value=111;
		float floval=value;
		double dval=value;
		long lval=value;
		System.out.println("byte value"+value);
		System.out.println("float value"+floval);
		System.out.println("double val"+dval);
		System.out.println("long val"+lval);
		System.out.println("");
		
		short value1=1222;
		float fval1=value1;
		double dval1=value1;
		long lval1=value1;
		System.out.println("short value"+value1);
		System.out.println("float value"+fval1);
		System.out.println("double val"+dval1);
		System.out.println("long val"+lval1);

		
		
		
		


	}

}
