package com.simplilearn.typecasting;

public class TypeCastDemo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		long count =1999999;
		byte bcount=(byte) count;
		short scount=(short) count;
		double dcount=count;
		System.out.println("long count is"+count);
		System.out.println("byte count is"+bcount);
		System.out.println("short count is"+scount);
		System.out.println("double count is"+dcount);
		System.out.println(" ");
		
		float sal=(float) 11.1;
		byte bsal=(byte) sal;
		short ssal=(short) sal;
		double dsal=sal;
		System.out.println(" float salary is"+sal);
		System.out.println("byte  salary is"+bsal);
		System.out.println("short salary is"+ssal);
		System.out.println("double salary is"+dsal);
		System.out.println(" ");
		
		int sum=1434;
		byte bsum=(byte) sum;
		short ssum=(short) sum;
		double dsum=sum;
		System.out.println("int sum is"+sum);
		System.out.println("byte sum is"+bsum);
		System.out.println("short sum is"+ssum);
		System.out.println("double sum is"+dsum);
		
		

		
		
		
		
		
	}

}
